-- Banking Tables Schema and Sample Data (UK)
-- Dataset: autonomous_data_analyst
-- Run with: bq query --use_legacy_sql=false < scripts/create_banking_tables.sql

-- ============================================
-- 1. BRANCHES TABLE
-- ============================================
CREATE OR REPLACE TABLE `autonomous_data_analyst.branches` (
  branch_id STRING NOT NULL,
  branch_name STRING NOT NULL,
  address STRING,
  city STRING,
  county STRING,
  postcode STRING,
  phone STRING,
  manager_name STRING,
  opened_date DATE,
  is_active BOOL DEFAULT TRUE
);


-- ============================================
-- 2. CUSTOMERS TABLE
-- ============================================
CREATE OR REPLACE TABLE `autonomous_data_analyst.customers` (
  customer_id STRING NOT NULL,
  first_name STRING NOT NULL,
  last_name STRING NOT NULL,
  email STRING,
  phone STRING,
  date_of_birth DATE,
  address STRING,
  city STRING,
  county STRING,
  postcode STRING,
  ni_number_last_four STRING,
  created_at TIMESTAMP,
  updated_at TIMESTAMP,
  is_active BOOL DEFAULT TRUE,
  credit_score INT64,
  annual_income NUMERIC,
  employment_status STRING,
  primary_branch_id STRING
);


-- ============================================
-- 3. ACCOUNTS TABLE
-- ============================================
CREATE OR REPLACE TABLE `autonomous_data_analyst.accounts` (
  account_id STRING NOT NULL,
  customer_id STRING NOT NULL,
  account_type STRING NOT NULL,
  account_number STRING NOT NULL,
  sort_code STRING,
  balance NUMERIC DEFAULT 0.00,
  currency STRING DEFAULT 'GBP',
  interest_rate NUMERIC,
  opened_date DATE,
  closed_date DATE,
  status STRING DEFAULT 'Active',
  branch_id STRING,
  overdraft_limit NUMERIC DEFAULT 0.00,
  last_transaction_date DATE
);


-- ============================================
-- 4. TRANSACTION_CATEGORIES TABLE
-- ============================================
CREATE OR REPLACE TABLE `autonomous_data_analyst.transaction_categories` (
  category_id STRING NOT NULL,
  category_name STRING NOT NULL,
  category_type STRING NOT NULL,
  description STRING,
  parent_category_id STRING
);


-- ============================================
-- 5. TRANSACTIONS TABLE
-- ============================================
CREATE OR REPLACE TABLE `autonomous_data_analyst.transactions` (
  transaction_id STRING NOT NULL,
  account_id STRING NOT NULL,
  transaction_type STRING NOT NULL,
  amount NUMERIC NOT NULL,
  transaction_date TIMESTAMP NOT NULL,
  description STRING,
  category_id STRING,
  merchant_name STRING,
  status STRING DEFAULT 'Completed',
  reference_number STRING,
  balance_after NUMERIC
);

